import { useMutation } from '@tanstack/react-query';
import { login } from '../api/auth';
import { LoginRequest, LoginResponse } from '../types/auth';

export const useLogin = () => {
    return useMutation<LoginResponse, Error, LoginRequest>({
      mutationFn: login,
    });
  };
