import React from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { loginSchema } from '../schemas/loginSchema';
import { z } from 'zod';
import { Input } from './ui/Input';
import { Button } from './ui/Button';
import { useLogin } from '../hooks/useLogin';

export const LoginForm = () => {
  const { mutate, isLoading, error } = useLogin();

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<z.infer<typeof loginSchema>>({
    resolver: zodResolver(loginSchema),
  });

  const onSubmit = (data: z.infer<typeof loginSchema>) => {
    mutate(data);
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)} className="max-w-sm mx-auto mt-20 p-6 border rounded-md">
      <h2 className="text-2xl font-bold mb-6 text-center">Login</h2>
      <Input label="Email" registration={register('email')} error={errors.email?.message} type="email" />
      <Input label="Password" registration={register('password')} error={errors.password?.message} type="password" />
      {error && <p className="text-red-500 text-sm mt-2">{error.message}</p>}
      <Button type="submit" disabled={isLoading}>{isLoading ? 'Logging in...' : 'Login'}</Button>
    </form>
  );
};
