// src/hooks/useLogin.ts
import { useMutation } from '@tanstack/react-query';
import { login } from '../api/auth';
import { LoginRequest, LoginResponse } from '../types/auth';

export const useLogin = () => {
  const mutation = useMutation({
    mutationFn: login, // This is the function that performs the mutation (e.g., logging in)
    onError: (error: Error) => {
      console.error('Login error:', error.message);
    },
  });

  return {
    mutate: mutation.mutate,       // use `mutation.mutate` to trigger the login
    isPending: mutation.isLoading, // Use `isLoading` to check if the mutation is in progress
    error: mutation.error,         // Get the error object from the mutation
  };
};
